import numpy as np 
import math 
in_array = [ 0, math.pi/2 ,np.pi/3 , np.pi]
print("Input array : ", in_array)
sin_values = np.sin(in_array)
print(" \n sine value : \n ", sin_values)

in_array = [ 0, math.pi/2 ,np.pi/3 , np.pi]
print("Input array : ", in_array)
cos_values = np.cos(in_array)
print(" \n cosine value : \n ", cos_values)


in_array = [ 0, math.pi/2 ,np.pi/3 , np.pi]
